CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."metrc_transfers"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "company_id" uuid NOT NULL, "license_id" uuid NOT NULL, "created_date" date NOT NULL, "delivery_id" text NOT NULL, "manifest_number" text NOT NULL, "transfer_payload" json NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("company_id") REFERENCES "public"."companies"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("license_id") REFERENCES "public"."company_licenses"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("delivery_id"), UNIQUE ("id")); COMMENT ON TABLE "public"."metrc_transfers" IS E'List of transfers from Metrc';
