CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."company_inventory_summaries"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "company_id" uuid NOT NULL, "date" date NOT NULL, "methodology" text NOT NULL, "default_methodology" text NOT NULL, "counts_payload" json NOT NULL, "inventory_accuracy_payload" json NOT NULL, "cogs_revenue_payload" json NOT NULL, "stale_info_payload" json NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("company_id") REFERENCES "public"."companies"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"), UNIQUE ("company_id", "date", "methodology"));
