CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."files"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "sequential_id" integer, "company_id" uuid NOT NULL, "name" text NOT NULL, "path" text NOT NULL, "extension" text NOT NULL, "size" bigint NOT NULL, "mime_type" text, "created_by_user_id" uuid NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("created_by_user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("company_id") REFERENCES "public"."companies"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id")); COMMENT ON TABLE "public"."files" IS E'Table to keep track of files stored on the platform';
