CREATE TABLE "public"."async_jobs" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "name" text NOT NULL, "queued_at" timestamptz NOT NULL DEFAULT now(), "started_at" timestamptz, "ended_at" Timestamp, "is_deleted" bool, "submitted_by_user_id" uuid, "status" text NOT NULL, "is_high_priority" bool NOT NULL DEFAULT false, "job_payload" json, "retry_payload" json, "err_details" json, "num_retries" Integer NOT NULL DEFAULT 0, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), "deleted_at" timestamptz, PRIMARY KEY ("id") , FOREIGN KEY ("submitted_by_user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));
