CREATE TABLE "public"."audit_trails" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "changed_table" text NOT NULL, "changed_by_user_id" uuid NOT NULL, "change_date" date NOT NULL, "changed_row_id" uuid NOT NULL, "metadata_info" json NOT NULL DEFAULT json_build_object(), "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), "deleted_at" timestamptz, PRIMARY KEY ("id") , FOREIGN KEY ("changed_by_user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));COMMENT ON TABLE "public"."audit_trails" IS E'This is used for tracking when different tables make changes where we want to preserve the history';
CREATE EXTENSION IF NOT EXISTS pgcrypto;
